pragma solidity >=0.4.21 <0.6.0;
pragma experimental ABIEncoderV2;

contract Cow{

    address owner;
    
    struct  Vaquinha{ 
        uint id;
        address criador;
        uint meta;
        uint saldo;
        bool destaque;
    }

    mapping(address => Vaquinha) vaquinhasPorEndereco;

    Vaquinha[] vaquinhas;

    event CriarVaquinha(address criador, uint meta);
    event SacarVaquinha(address criador);

    constructor() public {
        owner = msg.sender;
    }

    modifier onlyOwner {
        require(msg.sender == owner);
        _;
    }

    function criarVaquinha(uint _meta, bool _destaque) public payable {
        require(msg.value > 0 ether);
        vaquinhas.push(Vaquinha(0, msg.sender, _meta, 0, _destaque));
        vaquinhasPorEndereco[msg.sender] = Vaquinha(0, msg.sender, _meta, 0, _destaque);
        emit CriarVaquinha(msg.sender, _meta);
    }

    function getVaquinhas() public view returns (Vaquinha[] memory){
        return vaquinhas;
    }


    function getVaquinhaByAddress(/*address _criador*/) public view returns(Vaquinha memory)
    {
        return Vaquinha(0, msg.sender, 0, 0, true);
    }



}
